import React from "react";
import { useState } from "react";
import ReactDOM from "react-dom";
import useHover from "../hooks/useHover";
const Tooltip = ({ children, text }) => {
  const { hovered, nodeRef } = useHover();
  const [coords, setCoords] = useState({});
  const handleHover = (e) => {
    setCoords(e.target.getBoudingClientRect());
  };
  return (
    <div>
      {hovered && <TooltipContent children={children} coords></TooltipContent>}
      <span className="font-semibold" ref={nodeRef} onMouseOver={handleHover}>
        {text}
      </span>
    </div>
  );
};
const TooltipContent = ({ children, coords }) => {
  if (typeof document === "undefined") return null;
  return ReactDOM.createPortal(
    <p className="p-3 bg-black text-white rounded-xl inline-block">
      {children}
    </p>,
    document.querySelector("body")
  );
};
export default Tooltip;
