import { useState } from "react";
import useClickOutSide from "../hooks/useClickOutSide";

const DropdownPortal = () => {
  const { nodeRef, show, setShow } = useClickOutSide();
  const [coords, setCoords] = useState({});
  const handleClick = (e) => {
    console.log(nodeRef.current.getBoundingClientRect());
    setCoords(nodeRef.current.getBoundingClientRect());
    setShow(true);
  };
  return (
    <div className="p-5">
      <div className="relative w-full max-w-[500px]" ref={nodeRef}>
        <div
          className="p-5 border border-gray-500 rounded-lg w-full cursor-pointer"
          onClick={handleClick}
        >
          Selected
        </div>
        {show && (
          <div
            className="border border-gray-500 rounded-lg absolute top-full left-0 w-full bg-white"
            style={{
              left: coords.left,
            }}
          >
            <div className="p-5 cursor-pointer">JavaScript</div>
            <div className="p-5 cursor-pointer">ReactJS</div>
            <div className="p-5 cursor-pointer">VueJS</div>
            <div className="p-5 cursor-pointer">AngularJS</div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DropdownPortal;
