import React, { Fragment } from "react";
import "./App.css";
import SideBarMenuWithHook from "./components/SideBarMenuWithHook";
import useClickOutSide from "./hooks/useClickOutSide";

function App() {
  const { nodeRef, show, setShow } = useClickOutSide();
  return (
    <Fragment>
      <button onClick={() => setShow(!show)}>Side bar</button>
      <SideBarMenuWithHook show={show}></SideBarMenuWithHook>
    </Fragment>
  );
}

export default App;
