import React from "react";
import { useState } from "react";

const SideBarMenu = () => {
  const [showMenu, setShowMenu] = useState(false);
  return (
    <div>
      <button className="inline-block m-3 p-3 rounded-lg text-white bg-green-500">
        Show menu
      </button>
      <div
        className={`w-[300px] bg-gray-300 fixed top-0 left-0 bottom-0 z-10 -translate-x-full`}
      ></div>
    </div>
  );
};

export default SideBarMenu;
