import React from "react";
import {
  createUserWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
  updateProfile,
} from "firebase/auth";
import { useState } from "react";
import { auth } from "./firebase-config";
import { useEffect } from "react";

const FirebaseAuth = () => {
  const [values, setValues] = useState({
    email: "",
    password: "",
  });
  const [userInfo, setUserInfo] = useState();
  useEffect(() => {}, []);
  onAuthStateChanged(auth, (currentUser) => {
    setUserInfo(currentUser);
  });
  const handleInputChange = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    });
  };
  const handleCreateUser = async (e) => {
    e.preventDefault();
    const user = await createUserWithEmailAndPassword(
      auth,
      values.email,
      values.password
    );
    await updateProfile(auth.currentUser, {
      displayName: "Huy",
    });
    setUserInfo(user);
    console.log("success!!!");
  };
  const handleSignOut = () => {
    signOut(auth);
  };
  return (
    <div className="py-10">
      <div className="w-full max-w-[500px] mx-auto bg-white shadow-lg p-5 mb-10">
        <form onSubmit={handleCreateUser}>
          <input
            type="email"
            className="w-full p-3 mb-5 border border-gray-500 rounded outline-none focus:border-blue-500"
            placeholder="Enter your email address"
            name="email"
            onChange={handleInputChange}
          />
          <input
            type="password"
            className="w-full p-3 mb-5 border border-gray-500 rounded outline-none focus:border-blue-500"
            placeholder="Enter your password"
            name="password"
            onChange={handleInputChange}
          />
          <button className="w-full p-3 text-lg font-medium text-white bg-blue-500 rounded-lg">
            Sign Up
          </button>
        </form>
        <div className="flex items-center mt-10">
          <span>{userInfo?.displayName}</span>
          <button
            className="w-full p-3 text-lg font-medium text-white bg-purple-500 rounded-lg"
            onClick={handleSignOut}
          >
            SignOut
          </button>
        </div>
      </div>
    </div>
  );
};

export default FirebaseAuth;
