import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
const HackerNewsWithHook = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const handleFetchData = useRef({});
  handleFetchData.current = async () => {
    setLoading(true);
    try {
      const respone = await axios.get(
        `https://hn.algolia.com/api/v1/search?query=${query}`
      );
      setHits(respone.data.hits || []);
      setLoading(false);
    } catch (error) {
      setErrorMessage(`The error happend ${error}`);
    }
  };
  useEffect(() => {
    handleFetchData.current();
  }, [query]);
  return (
    <div>
      <input
        type="text"
        className="border border-gray-500 p-3 w-[500px]"
        onChange={(e) => setQuery(e.target.value)}
      />
      <p>{errorMessage}</p>
      {hits.length > 0 &&
        hits.map((item) => <h3 key={item.created_at}>{item.title}</h3>)}
    </div>
  );
};

export default HackerNewsWithHook;
