import { useState } from "react";
import { useContext } from "react";
import { createContext } from "react";
import "./App.css";

const CountContext = createContext();

function CountProvider(props) {
  const [count, setCount] = useState(0);
  const value = [count, setCount];
  return (
    <CountContext.Provider value={value} {...props}></CountContext.Provider>
  );
}
function useCount() {
  const context = useContext(CountContext);
  if(typeof context === "undefined") throw new Error("useCount must be used with a Count")
}
function CountDisplay() {
  // const count = 0;
  const [count] = useContext(CountContext);
  return <div>The count is: {count}</div>;
}
function Counter() {
  const [, setCount] = useContext(CountContext);
  // const setCount = () => {};
  const increment = () => setCount((c) => c + 1);
  return (
    <button
      onClick={increment}
      className="p-4 text-white bg-blue-500 text-lg rounded-lg"
    >
      Increment count
    </button>
  );
}

function App() {
  return (
    <div className="p-4 flex items-center justify-center gap-x-5">
      <CountProvider>
        <CountDisplay></CountDisplay>
        <Counter></Counter>
      </CountProvider>
    </div>
  );
}

export default App;
