import { useState } from "react";

const { useContext } = require("react");
const { createContext } = require("react");

const fakeData = [
  {
    id: 1,
    url: "https://images.unsplash.com/photo-1674497124192-c9b6d1000fd6?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1122&q=80",
    isFavorite: false,
  },
  {
    id: 2,
    url: "https://images.unsplash.com/photo-1674505921901-e54fe119f0a4?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    isFavorite: false,
  },
  {
    id: 3,
    url: "https://images.unsplash.com/photo-1674458884347-0b5460be866b?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1171&q=80",
    isFavorite: false,
  },
  {
    id: 4,
    url: "https://images.unsplash.com/photo-1674423094915-2546893e2636?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80",
    isFavorite: false,
  },
];
const GalleryContext = createContext();
function GalleryProvider(props) {
  const [photos, setPhotos] = useState(fakeData);
  const [cartItems, setCartItems] = useState([]);
  const [favoriteLisr, setFavoriteList] = useState([]);
  function toggleFavorite(photoId) {
    const updateArray = photos.map((photo) => {
      if (photo.id === photoId) {
        return { ...photo, isFavorite: !photo.isFavorite };
      }
      return photo;
    });
    setPhotos(updateArray);
  }
  function addToCart(newItem) {
    setCartItems((prevItems) => {
      const isExisted = prevItems.some(item)
      if (prevItems.includes(newItem)) return [...prevItems];
      return [...prevItems, newItem];
    });
  }
  const value = {
    photos,
    cartItems,
    favoriteLisr,
    setPhotos,
    setCartItems,
    setFavoriteList,
    toggleFavorite,
    addToCart,
  };
  return (
    <GalleryContext.Provider value={value} {...props}></GalleryContext.Provider>
  );
}
function useGallery() {
  const context = useContext(GalleryContext);
  if (typeof context === "undefined")
    throw new Error("useGallery must be used within a CountProvider");
  return context;
}
