//   // console.log(props);
//   return (
//     <Controller
//       name={name}
//       control={control}
//       defaultValue=""
//       render={({ field }) => (
//         <input
//           className="p-4 border border-gray-500 rounded-md"
//           {...field}
//           {...props}
//         />
//       )}
//     ></Controller>
//   );
// };

const MyInput = ({ name, control, ...props }) => {
  const { field } = useController({ control, name, defaultValue: "" });
  return (
    <input
      className="p-4 border border-gray-500 rounded-md"
      {...field}
      {...props}
    />
  );
};

export default SignUpFormHookWithYupV2;
