import { useContext } from "react";
import { useState } from "react";
import { createContext } from "react";

const AuthContext = createContext();
function AuthProvider(props) {
  const [user, setUser] = useState({
    userId: 1,
    fullname: "Tôm",
    email: "tom@gmail.com",
    avatar:
      "https://images.unsplash.com/photo-1674231386741-211eec6d6173?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=686&q=80",
  });
  const value = { user, setUser };
  return <AuthContext.Provider value={value} {...props}></AuthContext.Provider>;
}
function useAuth() {
  const context = useContext(AuthContext);
  if (typeof context === "undefined")
    throw new Error("useAuth must be used within a CProvider");
  return context;
}
export { AuthProvider, useAuth };
