import React, { useRef, useState } from "react";

const TextareaAutoResize = () => {
  const [text, setText] = useState("demo");
  const textareaRef = useRef(null);
  const [textareaHeight, setTextareaHeight] = useState("auto");
  const [textareaWrapperHeight, setTextareaWrapperHeight] = useState("auto");
  return (
    <div className="p-5">
      <textarea
        className="w-full max-w-[400px] p-5 rounded-lg border border-gray-500 resize-none focus:border-blue-500"
        placeholder="Please your enter content..."
        value={text}
        ref={textareaRef}
        style={{
          height: textareaHeight,
        }}
      ></textarea>
    </div>
  );
};

export default TextareaAutoResize;
