import React from "react";

const TextareaAutoResize = () => {
  return (
    <div className="p-5">
      <textarea
        className="w-full max-w-[400px] p-5 rounded-lg border border-gray-500 resize-none focus:border-blue"
        placeholder="Please your enter content..."
      ></textarea>
    </div>
  );
};

export default TextareaAutoResize;
