import { createContext } from "react";
import "./App.css";

const CountContext = createContext();
function CountProvider(props) {
  return <CountContext.Provider {...props}></CountContext.Provider>;
}
function CountDisplay() {
  const count = 0;
  return <div>The count is: {count}</div>;
}
function Counter() {
  const setCount = () => {};
  const increment = () => setCount((c) => c + 1);

  return (
    <button
      onClick={increment}
      className="p-4 text-white bg-blue-500 text-lg rounded-lg"
    >
      Increment count
    </button>
  );
}

function App() {
  return (
    <div className="p-4 flex items-center justify-center gap-x-5">
    <CountProvider>

    </CountProvider>
      <CountDisplay></CountDisplay>
      <Counter></Counter>
    </div>
  );
}

export default App;
