import React from "react";
import { useAuth } from "../context/authContext";

const HeaderMain = () => {
  const { user, setUser } = useAuth();
  return (
    <div className="flex items-center justify-between p-4 bg-white shadow-md">
      {user ? (
        <div className="flex items-center gap-x-3">
          <img
            src="https://images.unsplash.com/photo-1677690092396-e1efd8a3bc01?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
            alt=""
            className="object-cover w-10 h-10 rounded-full"
          />
          <span className="text-sm font-medium">
            Welcome back! <strong>{user.fullname}</strong>
          </span>
        </div>
      ) : (
        <span className="text-sm font-medium">Welcome</span>
      )}
      <button className="p-2 bg-gray-400 rounded-md">Sign out</button>
    </div>
  );
};

export default HeaderMain;
