import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
const HackerNewsWithHook = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const [url, setUrl] = useState(
    `https://hn.algolia.com/api/v1/search?query=${query}`
  );
  const handleFetchData = useRef({});
  const isMounted = useRef(true);
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  });
  handleFetchData.current = async () => {
    setLoading(true);
    try {
      const respone = await axios.get(url);
      if (!isMounted.current) {
        setHits(respone.data.hits || []);
        setLoading(false);
      }
    } catch (error) {
      setLoading(false);
      setErrorMessage(`The error happend ${error}`);
    }
  };
  useEffect(() => {
    handleFetchData.current();
  }, [url]);
  return (
    <div className="p-5">
      <div className="flex gap-x-4">
        <input
          type="text"
          className="border border-gray-500 p-3 w-[500px]"
          placeholder="Typing your keyword..."
          defaultValue={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white p-3 text-xl"
          onClick={() =>
            setUrl(`https://hn.algolia.com/api/v1/search?query=${query}`)
          }
        >
          Fetch
        </button>
      </div>
      {loading && (
        <div className="w-8 h-8 border-4 border-blue-500 rounded-full border-r-transparent animate-spin"></div>
      )}
      <p>{errorMessage}</p>
      {!loading &&
        hits.length > 0 &&
        hits.map((item) => <h3 key={item.created_at}>{item.title}</h3>)}
    </div>
  );
};

export default HackerNewsWithHook;
