import { Fragment } from "react";
import "./App.css";
import CartList from "./components/gallery/CartList";
import PhotoList from "./components/gallery/PhotoList";
import HeaderMain from "./components/HeaderMain";
import { AuthProvider } from "./contexts/authContext";
import { CountProvider, useCount } from "./contexts/countContext";
import { GalleryProvider } from "./contexts/galleryContext";

function CountDisplay() {
  // const count = 0;
  const { count } = useCount();
  return <div>The count is: {count}</div>;
}
function Counter() {
  const { setCount } = useCount();
  // const setCount = () => {};
  const increment = () => setCount((c) => c + 1);
  return (
    <button
      onClick={increment}
      className="p-4 text-white bg-blue-500 text-lg rounded-lg"
    >
      Increment count
    </button>
  );
}

function App() {
  return (
    <Fragment>
      {/* <div className="p-4 flex items-center justify-center gap-x-5">
        <CountProvider>
          <CountDisplay></CountDisplay>
          <Counter></Counter>
        </CountProvider>
      </div> */}
      {/* <AuthProvider>
        <HeaderMain></HeaderMain>
      </AuthProvider> */}
      <GalleryProvider>
        <AuthProvider>
          <HeaderMain></HeaderMain>
        </AuthProvider>
        <PhotoList></PhotoList>
        <CartList></CartList>
      </GalleryProvider>
    </Fragment>
  );
}

export default App;
