import { useState } from "react";
import { Fragment } from "react";
import "./App.css";
import DropdownPortal from "./components/DropdownPortal";
import Modal from "./components/modal/Modal";
//   color: {
//     blue: "#ffa440",
//   },
//   pink: "#ff6bcb",
// };
function App() {
  const [showModal, setShowModal] = useState(false);
  return (
    <Fragment>
      <div className="relative z-0">
        <Modal open={showModal} handleClose={() => setShowModal(false)}></Modal>
      </div>
      <button
        className="p-4 bg-blue-500 text-white rounded-lg"
        onClick={() => setShowModal(true)}
      >
        Show modal
      </button>
      <div className="relative z-30">
        Lorem ipsum, dolor sit amet consectetur adipisicing elit. Vero sit
        veniam consectetur! Possimus omnis dolorem veniam, quaerat blanditiis
        impedit quidem. Autem suscipit explicabo modi praesentium vel alias
        inventore illo in!
      </div>
      <div>
        <DropdownPortal></DropdownPortal>
      </div>
    </Fragment>
  );
}

export default App;
