import React from "react";
import ReactDOM from "react-dom";
const Modal = ({ open = false, handleClose = () => {} }) => {
  if (typeof document === "undefined") return <div className="modal"></div>;
  return ReactDOM.createPortal(
    <div className="modal fixed inset-0 z-50 flex items-center justify-center p-10">
      <div className="overlay absolute inset-0 bg-black bg-opacity-25"></div>
      <div className="modal-content bg-white relative z-10 rounded-lg p-10 w-[482px]">
        <span className="absolute right-0 top-0 cursor-pointer">Close</span>
        <h2 className="">Welcome Back!</h2>
      </div>
    </div>,
    document.querySelector("body")
  );
};

export default Modal;
