import { collection, getDocs } from "firebase/firestore";
import React from "react";
import { useEffect } from "react";
import { db } from "./firebase-config";

const FirebaseApp = () => {
  // colRef
  const colRef = collection(db, "posts");
  console.log(colRef);
  useEffect(() => {
    // 1. Get collection data (posts)
    getDocs(colRef)
      .then((snapshot) => {
        console.log(snapshot);
        let posts = [];
        snapshot.docs.forEach((doc) => {
          posts.push({
            id: doc.id,
            ...doc.data(),
          });
        });
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  const handleAddPost = (e) => {
    e.preventDefault();
    console.log("submit");
  };
  return (
    <div className="p-10">
      <div className="w-full max-w-[500px] mx-auto bg-white shadow-lg p-5">
        <form onSubmit={handleAddPost}>
          <input
            type="text"
            className="w-full p-3 mb-5 border border-gray-500 rounded outline-none focus:border-blue-500"
            placeholder="Enter your title"
            name="title"
          />
          <input
            type="text"
            className="w-full p-3 mb-5 border border-gray-500 rounded outline-none focus:border-blue-500"
            placeholder="Enter your author"
            name="author"
          />
          <button className="w-full p-3 text-lg font-medium text-white bg-blue-500 rounded-lg">
            Add post
          </button>
        </form>
      </div>
    </div>
  );
};

export default FirebaseApp;
