import React, { Fragment } from "react";
import "./App.css";
import CartList from "./components/gallary/CartList";
import PhotoList from "./components/gallary/PhotoList";
import HeaderMain from "./components/HeaderMain";
import { AuthProvider } from "./context/authContext";
import { CountProvider, useCount } from "./context/countContext";
import { GallaryProvider } from "./context/gallaryContext";

function CountDisplay() {
  const context = useCount();
  const { count } = context;
  return <div>The count is: {count}</div>;
}

function Counter() {
  // const setCount = () => {};
  // const increment = () => setCount((c) => c + 1);
  const context = useCount();
  const { count, setCount } = context;
  const increment = () => setCount(count + 1);
  return (
    <button
      onClick={increment}
      className="p-4 font-semibold text-white bg-purple-500"
    >
      Increment count
    </button>
  );
}
function App() {
  return (
    <Fragment>
      {/* <div className="flex items-center justify-center p-5 gap-x-5">
        <CountProvider>
          <CountDisplay></CountDisplay>
          <Counter></Counter>
        </CountProvider>
      </div> */}
      {/* <AuthProvider>
        <HeaderMain></HeaderMain>
      </AuthProvider> */}
      <GallaryProvider>
        <AuthProvider>
          <HeaderMain></HeaderMain>
        </AuthProvider>
        <PhotoList></PhotoList>
        <CartList></CartList>
      </GallaryProvider>
    </Fragment>
  );
}

export default App;
