// import logo from "./logo.svg";
import { useEffect, useRef, useState } from "react";
import "./App.css";
import Blog from "./components/Blog";
import Dropdown from "./components/Dropdown";
import DropdownWithHook from "./components/DropdownWithHook";
import Input from "./components/Input";
import HackerNews from "./components/news/HackerNews";
import HackerNews2 from "./components/news/HackerNews2";
import HackerNewsWithHook from "./components/news/HackerNewsWithHook";
import SideBarMenu from "./components/SideBarMenu";
import StopWatch from "./components/stopwatch/StopWatch";
import TextareaAutoResize from "./components/TextareaAutoResize";
import useClickOutSide from "./hooks/useClickOutSide";
// const theme = {
//   color: {
//     blue: "#ffa440",
//   },
//   pink: "#ff6bcb",
// };
function App() {
  const { show, setShow, nodeRef } = useClickOutSide();
  return (
    <div>
      <button
        onClick={() => setShow(true)}
        className="inline-block m-3 p-3 rounded-lg text-white bg-green-500"
      >
        Show Menu
      </button>
      <SideBarMenu show={show} ref={nodeRef}></SideBarMenu>
    </div>
  );
}

export default App;
