import React from "react";
import { useEffect } from "react";
import { createPortal } from "react-dom";
function createPortalWrapper() {
  const element = document.createElement("div");
  element.id = "portal-wrapper";
  return element;
}
const portalWrapperElm = createPortalWrapper();
const Portal = ({ containerClassName = "" }) => {
  useEffect(() => {
    document.body.appendChild(portalWrapperElm);
  }, []);
  const renderContent = (
    <div
      className="fixed inset-0 "
      style={{
        zIndex: "9999",
      }}
    >
      <div className="overplay absolute inset-0 bg-black bg-opacity-20"></div>
      <div className="content relative z-10">
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Dicta nemo
        nihil alias autem provident optio cupiditate iste, magni porro earum
        repellat voluptatem iusto distinctio omnis officia natus! Dolores,
        dolorem aperiam!
      </div>
    </div>
  );
  return createPortal(renderContent, portalWrapperElm);
};

export default Portal;
