import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("react");
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const hanldeFetchData = useRef({});
  hanldeFetchData.current = async () => {
    setLoading(true);
    try {
      const respone = await axios.get(
        `https://hn.algolia.com/api/v1/search?query=${query}`
      );
      // console.log(respone);
      setHits(respone.data.hits || []);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      setErrorMessage("")
    }
  };
  useEffect(() => {
    hanldeFetchData.current();
  }, [query]);
  return (
    <div>
      <input
        type="text"
        className="border border-green-500 text-black p-5 mb-5"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      {loading && (
        <div className="w-8 h-8 rounded-full border-4 border-blue-500 border-r-transparent animate-spin"></div>
      )}
      {!loading &&
        hits.length > 0 &&
        hits.map((item, index) => <h3 key={item.title}>{item.title}</h3>)}
    </div>
  );
};

export default HackerNews;
